import os
from dotenv import load_dotenv
from utils.error_handling import handle_error, critical_error
import logging

script_dir = os.path.dirname(os.path.abspath(__file__))
project_dir = os.path.dirname(script_dir)
dotenv_path = os.path.join(project_dir, '.env')

def reload_env():
    load_dotenv(dotenv_path, override=True)

reload_env()

class Config:
    MT5_LOGIN = os.getenv("MT5_LOGIN")
    if not MT5_LOGIN:
        raise ValueError("MT5_LOGIN environment variable is not set.")

    MT5_PASSWORD = os.getenv("MT5_PASSWORD")
    if not MT5_PASSWORD:
        raise ValueError("MT5_PASSWORD environment variable is not set.")

    MT5_SERVER = os.getenv("MT5_SERVER")
    if not MT5_SERVER:
        raise ValueError("MT5_SERVER environment variable is not set.")

    MT5_PATH = os.getenv("MT5_PATH")
    if not MT5_PATH:
        raise ValueError("MT5_PATH environment variable is not set.")

    MT5_TIMEFRAME = os.getenv("MT5_TIMEFRAME")
    if MT5_TIMEFRAME not in ["M1", "M5", "M15", "M30", "H1", "H4", "D1"]:
        raise ValueError(f"Invalid MT5_TIMEFRAME value: {MT5_TIMEFRAME}. Expected values: M1, M5, M15, M30, H1, H4, D1.")

    SYMBOLS = os.getenv("SYMBOLS")
    if SYMBOLS:
        SYMBOLS = SYMBOLS.split(",")
    else:
        raise ValueError("SYMBOLS environment variable is not set.")

    TELEGRAM_TOKEN = os.getenv("TELEGRAM_BOT_TOKEN")
    TELEGRAM_IDS = os.getenv("TELEGRAM_IDS")
    if TELEGRAM_TOKEN and TELEGRAM_IDS:
        TELEGRAM_IDS = TELEGRAM_IDS.split(",")
    else:
        TELEGRAM_TOKEN = None
        TELEGRAM_IDS = None

    try:
        MIN_TP_PROFIT = float(os.getenv("MIN_TP_PROFIT", 50.0))
    except (ValueError, TypeError):
        raise ValueError(f"Invalid MIN_TP_PROFIT value: {os.getenv('MIN_TP_PROFIT')}. Expected a numeric value.")

    try:
        MAX_LOSS_PER_DAY = float(os.getenv("MAX_LOSS_PER_DAY", 1000.0))
    except (ValueError, TypeError):
        raise ValueError(f"Invalid MAX_LOSS_PER_DAY value: {os.getenv('MAX_LOSS_PER_DAY')}. Expected a numeric value.")

    try:
        STARTING_EQUITY = float(os.getenv("STARTING_EQUITY", 10000.0))
    except (ValueError, TypeError):
        raise ValueError(f"Invalid STARTING_EQUITY value: {os.getenv('STARTING_EQUITY')}. Expected a numeric value.")

    try:
        LIMIT_NO_OF_TRADES = int(os.getenv("LIMIT_NO_OF_TRADES", 5))
    except (ValueError, TypeError):
        raise ValueError(f"Invalid LIMIT_NO_OF_TRADES value: {os.getenv('LIMIT_NO_OF_TRADES')}. Expected an integer value.")

    try:
        RISK_PER_TRADE = float(os.getenv("RISK_PER_TRADE", 0.01))
    except ValueError:
        raise ValueError(f"Invalid RISK_PER_TRADE value: {os.getenv('RISK_PER_TRADE')}. Expected a numeric value.")

    if not 0 < RISK_PER_TRADE <= 1:
        raise ValueError(f"RISK_PER_TRADE value must be between 0 and 1. Current value: {RISK_PER_TRADE}")

    try:
        PIP_VALUE = float(os.getenv("PIP_VALUE", 1))
    except ValueError:
        raise ValueError(f"Invalid PIP_VALUE value: {os.getenv('PIP_VALUE')}. Expected a numeric value.")

    @classmethod
    def validate(cls):
        try:
            required_vars = [
                'MT5_LOGIN', 'MT5_PASSWORD', 'MT5_SERVER', 'MT5_PATH',
                'MT5_TIMEFRAME', 'SYMBOLS'
            ]
            for var in required_vars:
                if not getattr(cls, var, None):
                    raise ValueError(f"Missing required environment variable: {var}")

            numeric_vars = ['MIN_TP_PROFIT', 'MAX_LOSS_PER_DAY', 'STARTING_EQUITY', 'RISK_PER_TRADE', 'PIP_VALUE']
            for var in numeric_vars:
                if not isinstance(getattr(cls, var, None), (int, float)):
                    raise ValueError(f"Invalid value for {var}. Expected a numeric value.")

            if not isinstance(cls.LIMIT_NO_OF_TRADES, int):
                raise ValueError(f"Invalid value for LIMIT_NO_OF_TRADES. Expected an integer value.")

        except ValueError as e:
            handle_error(e, "Configuration validation failed")
            critical_error(e, "Invalid configuration settings")


    @classmethod
    def log_config(cls):
        for attr, value in cls.__dict__.items():
            if not callable(value) and not attr.startswith("__") and not isinstance(value, classmethod):
                logging.info(f"{attr}: {value}")

try:
    Config.validate()
except Exception as e:
    handle_error(e, "Error occurred during configuration validation")
    raise

import schedule import time import logging from strategy.tunnel_strategy import run_strategy from metatrader.connection import initialize_mt5 from config import Config from utils.error_handling import handle_error, warn_error from strategy.trade_logic import calculate_position_size, entry_long, entry_short, exit_trade from utils.data_validation import validate_data, sanitize_data from metatrader.trade_management import get_open_positions, should_exit_position
def initialize_strategy(): try: logging.info("Initializing strategy on server: %s", Config.MT5_SERVER) mt5_init = initialize_mt5( login=Config.MT5_LOGIN, password=Config.MT5_PASSWORD, server=Config.MT5_SERVER, path=Config.MT5_PATH, )
run_strategy( symbols=Config.SYMBOLS, mt5_init=mt5_init, timeframe=Config.MT5_TIMEFRAME, lot_size=Config.MT5_LOT_SIZE, min_take_profit=Config.MIN_TP_PROFIT, max_loss_per_day=Config.MAX_LOSS_PER_DAY, starting_equity=Config.STARTING_EQUITY, max_traders_per_day=Config.LIMIT_NO_OF_TRADES ) except Exception as e: warn_error(e, "Error initializing strategy")
def run_scheduled_tasks(): while True: try: schedule.run_pending() time.sleep(1) except Exception as e: handle_error(e, "Error during scheduled task execution")
def run_trading_task(): try: account_info = get_account_info() account_balance = account_info["balance"]
for symbol in Config.SYMBOLS: data = get_historical_data(symbol, Config.MT5_TIMEFRAME, start_time, end_time) sanitized_data = sanitize_data(data)
if validate_data(sanitized_data, TradeRequestSchema.schema()): signal = generate_trade_signal(sanitized_data, period, deviation) price = sanitized_data["close"].iloc[-1] stop_loss_pips = 20 take_profit_pips = 40 deviation = 10 magic = 12345 comment = "Tunnel Strategy"
position_size = calculate_position_size(account_balance, Config.RISK_PER_TRADE, stop_loss_pips, Config.PIP_VALUE)
if signal == 'BUY': sl = price - (stop_loss_pips * Config.PIP_VALUE) tp = price + (take_profit_pips * Config.PIP_VALUE) entry_long(symbol, position_size, price, sl, tp, deviation, magic, comment) elif signal == 'SELL': sl = price + (stop_loss_pips * Config.PIP_VALUE) tp = price - (take_profit_pips * Config.PIP_VALUE) entry_short(symbol, position_size, price, sl, tp, deviation, magic, comment) else: logging.error("Invalid input data")
open_positions = get_open_positions() for position in open_positions: if should_exit_position(position): exit_trade(position.ticket)
except Exception as e: handle_error(e, "Failed to execute trading task or validate/sanitize input data")
def setup_schedule(): schedule.every().day.at("09:00").do(initialize_strategy) schedule.every(15).minutes.do(run_trading_task) logging.info("Scheduler setup complete. Next run at: %s", schedule.next_run())
def adjust_schedule(market_conditions): if market_conditions == 'volatile': schedule.every(5).minutes.do(run_trading_task) elif market_conditions == 'calm': schedule.every(30).minutes.do(run_trading_task)
# Call adjust_schedule() based on market conditions market_conditions = analyze_market_conditions() adjust_schedule(market_conditions)

import MetaTrader5 as mt5
import pandas as pd
import requests_cache
from datetime import datetime, timedelta
